// Extract request data
const body = $input.first().json.body;

return [{
  json: {
    sourceContentId: body.sourceContentId,
    sourceContent: body.sourceContent,
    sourceFormat: body.sourceFormat,
    targetFormats: body.targetFormats
  }
}];

// Split target formats into individual items for processing
const data = $input.first().json;
const targetFormats = data.targetFormats || [];

return targetFormats.map(format => ({
  json: {
    sourceContentId: data.sourceContentId,
    sourceContent: data.sourceContent,
    sourceFormat: data.sourceFormat,
    targetFormat: format
  }
}));

// Parse AI response
const aiResponse = $input.first().json.text;
const sourceContentId = $('Split Target Formats').item.json.sourceContentId;
const targetFormat = $('Split Target Formats').item.json.targetFormat;

let parsedContent;
try {
  parsedContent = JSON.parse(aiResponse);
} catch (e) {
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsedContent = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Failed to parse AI response as JSON');
  }
}

return [{
  json: {
    sourceContentId,
    format: targetFormat,
    variations: parsedContent.variations || []
  }
}];

// Combine all repurposed content into final response
const items = $input.all();
const sourceContentId = items[0]?.json?.sourceContentId;

const variations = items.map(item => ({
  format: item.json.format,
  content: item.json.variations[0]?.content || '',
  title: item.json.variations[0]?.title || item.json.format,
  allVariations: item.json.variations
}));

return [{
  json: {
    success: true,
    sourceContentId,
    variations
  }
}];
