// Prepare competitor and content data for analysis
const competitors = $('Fetch Competitor Domains').all();
const ourContent = $('Fetch Our Content Library').all();

// Extract our content titles and topics for comparison
const ourTopics = ourContent.map(item => ({
  title: item.json.title,
  contentType: item.json.content_type,
  keywords: item.json.keywords || []
}));

// Format competitor list
const competitorList = competitors.map(c => ({
  domain: c.json.domain,
  companyName: c.json.company_name || c.json.domain
}));

return [{
  json: {
    competitors: competitorList,
    ourContentSummary: {
      totalPieces: ourTopics.length,
      topics: ourTopics.slice(0, 30) // Limit for token management
    }
  }
}];

// Parse the AI response and format for Supabase insert
const aiResponse = $input.first().json.text;

let ideas;
try {
  ideas = JSON.parse(aiResponse);
} catch (e) {
  const jsonMatch = aiResponse.match(/\[\s*\{[\s\S]*\}\s*\]/);
  if (jsonMatch) {
    ideas = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Failed to parse AI response as JSON');
  }
}

const timestamp = new Date().toISOString();

// Format for Supabase insert
const formattedIdeas = ideas.map((idea, index) => ({
  json: {
    topic: idea.topic,
    gap_score: idea.gapScore,
    rationale: idea.rationale + (idea.competitorInsight ? ' Competitor insight: ' + idea.competitorInsight : ''),
    suggested_format: idea.suggestedFormat,
    status: 'new',
    category: 'competitor',
    generation_context: { competitorInsight: idea.competitorInsight },
    created_by: 'ai',
    created_at: timestamp
  }
}));

return formattedIdeas;

// Format response for webhook
const savedIdeas = $input.all();

return [{
  json: {
    success: true,
    category: 'competitor',
    ideas: savedIdeas.map(item => ({
      id: item.json.id,
      topic: item.json.topic,
      gapScore: item.json.gap_score,
      rationale: item.json.rationale,
      suggestedFormat: item.json.suggested_format
    }))
  }
}];
