// Parse the AI response and prepare for Supabase insert
const dealId = $('Catch Deal ID').first().json.body.deal_id;
const aiResponse = $input.first().json.message.content;

let parsedData;
try {
  // Try to parse the JSON response
  parsedData = JSON.parse(aiResponse);
} catch (error) {
  // If JSON parsing fails, try to extract JSON from markdown code blocks
  const jsonMatch = aiResponse.match(/```(?:json)?\s*([\s\S]*?)```/);
  if (jsonMatch) {
    parsedData = JSON.parse(jsonMatch[1]);
  } else {
    throw new Error('Failed to parse AI response as JSON');
  }
}

const answers = parsedData.answers || {};
const confidence = parsedData.confidence || {};

// Helper function to convert camelCase to snake_case
function toSnakeCase(str) {
  return str.replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);
}

// Build the Supabase insert object with snake_case column names
const supabaseData = {
  deal_id: dealId,
  company_name: answers.companyName || null,
  contact_name: answers.contactName || null,
  email_address: answers.emailAddress || null,
  entity_type: answers.entityType || null,
  has_restricted_grants: answers.hasRestrictedGrants || null,
  uses_qbo_or_xero: answers.usesQboOrXero || null,
  accounting_platform: answers.accountingPlatform || null,
  accounting_basis: answers.accountingBasis || null,
  bookkeeping_cadence: answers.bookkeepingCadence || null,
  needs_financials_before_15th: answers.needsFinancialsBefore15th || null,
  financial_review_frequency: answers.financialReviewFrequency || null,
  payroll_provider: answers.payrollProvider || null,
  has_401k: answers.has401k || null,
  payroll_departments: answers.payrollDepartments || null,
  employee_count: answers.employeeCount || null,
  tracks_expenses_by_employee: answers.tracksExpensesByEmployee || null,
  expense_platform: answers.expensePlatform || null,
  expense_platform_employees: answers.expensePlatformEmployees || null,
  needs_bill_pay_support: answers.needsBillPaySupport || null,
  bill_pay_cadence: answers.billPayCadence || null,
  bills_per_month: answers.billsPerMonth || null,
  needs_invoicing_support: answers.needsInvoicingSupport || null,
  invoicing_cadence: answers.invoicingCadence || null,
  invoices_per_month: answers.invoicesPerMonth || null,
  interested_in_cfo_review: answers.interestedInCfoReview || null,
  additional_notes: answers.additionalNotes || null,
  fireflies_video_link: answers.firefliesVideoLink || null,
  field_confidence: confidence,
  is_auto_filled: true,
  auto_filled_at: new Date().toISOString(),
  is_confirmed: false,
  confirmed_at: null
};

return [{
  json: {
    deal_id: dealId,
    supabase_data: supabaseData,
    raw_answers: answers,
    raw_confidence: confidence
  }
}];
