// Format the AI-generated workflow description with headers and metadata
const projectId = $('Catch Project ID').first().json.body.project_id;
const aiResponse = $input.first().json.message.content;

// Generate timestamp for filename
const now = new Date();
const timestamp = now
  .toISOString()
  .replace(/[-:]/g, '')
  .split('.')[0]
  .replace('T', '_');

const dateFormatted = now.toISOString().split('T')[0];
const timeFormatted = now.toTimeString().split(' ')[0];

// Construct plain text workflow description document
let formattedDocument = `===================================================
N8N WORKFLOW DESCRIPTION DOCUMENT
===================================================
Project ID: ${projectId}
Generated: ${dateFormatted} ${timeFormatted}
Generated By: AI Automation
===================================================

${aiResponse}

===================================================
End of Workflow Description
Generated automatically by GrowthLab AI Console
===================================================`;

// ✅ Normalize all text for UTF-8 and JSON compatibility
formattedDocument = formattedDocument
  // Replace smart quotes with straight quotes
  .replace(/[“”]/g, '"')
  .replace(/[‘’]/g, "'")
  // Replace en/em dashes with hyphen
  .replace(/[–—]/g, '-')
  // Replace ellipsis with three dots
  .replace(/…/g, '...')
  // Replace bullet points with a hyphen
  .replace(/•/g, '-')
  // Replace non-breaking spaces with normal ones
  .replace(/\u00A0/g, ' ')
  // Normalize line endings
  .replace(/\r?\n/g, '\n');

// ✅ Make it JSON-safe for Supabase
const jsonSafeFormatted = JSON.stringify(formattedDocument);

// ✅ Parse back to remove wrapping quotes (so stored as readable text)
const safeDocument = JSON.parse(jsonSafeFormatted);

return [
  {
    json: {
      project_id: projectId,
      formatted_workflow_description: safeDocument,
      raw_description_content: aiResponse,
      generation_timestamp: timestamp,
    },
  },
];

// Generate filename and storage path
const projectId = $('Catch Project ID').first().json.body.project_id;
const timestamp = $input.first().json.generation_timestamp;

const filename = `project_${projectId}_n8n_workflow_description_${timestamp}.txt`;
const storagePath = `${projectId}/workflow-description/${filename}`;

return [
  {
    json: {
      ...($input.first().json),
      generated_filename: filename,
      storage_path: storagePath,
      upload_path: storagePath
    }
  }
];

// Parse AI response and map sprint length to price
const projectId = $('Catch Project ID').first().json.body.project_id;

// The AI response structure from the screenshot
const aiMessage = $input.first().json.message;
const sprintLength = aiMessage.content.sprint_length;
const explanation = aiMessage.content.explanation;

// Sprint length to price mapping (matches frontend)
const SPRINT_PRICING = {
  "0.5": 3000,
  "1": 5000,
  "1.5": 6500,
  "2": 8000
};

let aiSprintLength = "1"; // Default fallback
let aiExplanation = "";
let aiPrice = 5000; // Default fallback

// Check if we have valid sprint length from AI
if (sprintLength && SPRINT_PRICING[sprintLength.toString()]) {
  aiSprintLength = sprintLength.toString();
  aiExplanation = explanation || "";
  aiPrice = SPRINT_PRICING[aiSprintLength];
} else {
  // Invalid or missing sprint length, use defaults
  aiExplanation = `AI returned invalid sprint length (${sprintLength}). Defaulted to 1 sprint.`;
  aiPrice = SPRINT_PRICING["1"];
}

return [
  {
    json: {
      project_id: projectId,
      ai_sprint_length: aiSprintLength,
      ai_price: aiPrice,
      ai_explanation: aiExplanation,
      raw_ai_response: JSON.stringify(aiMessage), // Store full message for debugging
      estimation_timestamp: new Date().toISOString()
    }
  }
];

